import React, { Component } from "react";
import Axios from "axios";
import Header from './Header';
import QrReader from 'react-qr-reader'
import { withRouter } from "react-router";
import GetAuthorisation from "../Funciton/GetAuthorisation";




class QrCode extends Component {


    state = {
		pharmacies: [],
		formShowed: false,
		name: "",
		city: "",
		openModal: false,
		openModalRefused: false,
		openModalCantUse: false,
		gradeFrom: "",
		gradeTo: "",
		distanceFrom: "",
		distanceTo: "",
		showingSearched: false,
		showingSorted: false,
		currentLatitude: null,
		currentLongitude: null,
		sortIndicator: 0,
		redirect:false,
		redirectUrl:''
	};

    componentDidMount() {

    }


    constructor(props){
        super(props)
        this.state = {
            delay: 100,
            result: 'No result',
        }
        this.handleScan = this.handleScan.bind(this)
    }

    openImageDialog() {
        this.refs.qrReader1.openImageDialog()
    }

    handleScan(data){
	    let RefuseReceiptDTO = {
	    	id: data
	    }
	    
        console.log(RefuseReceiptDTO);

        Axios.get("http://localhost:8080/drug/canPatientUseQR" + data, { headers: { Authorization: GetAuthorisation() } })
			.then((res) => {
				if(res.data){

                        console.log("sasaaaaaaaaaaaa");

                }else{
					this.setState({
						//openModalCantUse: true ,
					})
				}

			})
			.catch((err) => {
				console.log(err);
			});
		
    
	
    }



    render() {

        const previewStyle = {
            height: 240,
            width: 320,
        }
        return (
            <React.Fragment>
                <Header />

                <div className="container" style={{ marginTop: "10%" }}>
					<h5 className=" text-center mb-0 mt-2 text-uppercase">Enter QR code</h5>
       		
                        <QrReader ref="qrReader1"
                            delay={this.state.delay}
                            style={previewStyle}
                            onError={this.handleError}
                            onScan={this.handleScan}
                            legacyMode={true}
                        />
            
            </div>

            <div>
            <button
					type="button"
					onClick={this.openImageDialog.bind(this)}
					className="btn btn-outline-secondary mt-3"
				>
					Submit QR Code
				</button>
                </div>
           

        </React.Fragment>
    );

    }

}
export default withRouter(QrCode);
