import React, { Component } from "react";
import { Button, Modal } from "react-bootstrap";
import Axios from "axios";
import DatePicker from "react-datepicker";
import GetAuthorisation from "../../Funciton/GetAuthorisation";
import SuccessAlert from "../Alerts/SuccessfulAlert";
import UnsuccessAlert from "../Alerts/UnsuccessfulAlert";
import { Redirect } from "react-router-dom";
import { confirmAlert } from 'react-confirm-alert';
import "../card-components/card.styles.css"
import ConfirmModal from "./ConfirmModal";
const API_URL="http://localhost:8080";
class ScheduleModal extends Component {
	state = {
		workTimes: [],
		showAddWorkTime: false,
		selectedStartDate: new Date(),
		selectedEndDate: new Date(),
		timeFrom: 1,
		timeTo: 2,
		modalSize: "lg",
		hiddenSuccessAlert: true,
		successHeader: "",
		successMessage: "",
		hiddenFailAlert: true,
        s:true,
		failHeader: "",
		failMessage: "",
	};

    removeDermatologistClick = (id) =>{
        console.log("aaaaaaassssssssssssaaaaa");
        console.log(id);
        console.log("qqqqqqqqqqqqqqqq")
        confirmAlert({
            message: 'Are you sure to remove dermatologist.',
            buttons: [
              {
                label: 'Yes',
                onClick: () => {
                    let removeDermatologistDTO = {
                        pharmacyId : this.state.pharmacyId,
                        dermatologistId: id,
                    };
            
                    Axios
                    .put(API_URL + "/api/users/remove-dermatologist-from-pharmacy", removeDermatologistDTO, {
                        headers: { Authorization:  GetAuthorisation() },
                    }).then((res) =>{
                        console.log(res.data);
                        this.setState({
                            hiddenSuccessAlert: false,
                            hiddenFailAlert:true,
                            successHeader: "Success",
                            successMessage: "You successfully remove dermatologist.",
                        })
                        this.updateDermatologistList();
                        
                    }).catch((err) => {
                        this.setState({ 
                            hiddenSuccessAlert: true,
                            hiddenFailAlert: false, 
                            failHeader: "Unsuccess", 
                            failMessage: "It is not possible to remove the dermatologist"});

                    });
                }
              },
              {
                label: 'No',
                onClick: () => {
                    
                }
              }
            ],
            overlayClassName: "overlay-custom-class-name"
        });
    };
	handleClickOnClose = () => {
		this.setState({
			showAddWorkTime: false,
			modalSize: "lg",
			selectedStartDate: new Date(),
			selectedEndDate: new Date(),
			timeFrom: 1,
			timeTo: 2,
			hiddenSuccessAlert: true,
			hiddenFailAlert: true,
		});
		this.props.onCloseModal();
	};

	handleOpenAlertSuccess = () => {
		this.setState({ hiddenSuccessAlert: false ,showAddWorkTime: true,s:false});
	};

	handleCloseAlertSuccess = () => {
		this.setState({ hiddenSuccessAlert: true });
	};

	handleCloseAlertFail = () => {
		this.setState({ hiddenFailAlert: true });
	};

	render() {
		return (
			<Modal
				show={this.props.show}
				size={this.state.modalSize}
				dialogClassName="modal-120w-100h"
				aria-labelledby="contained-modal-title-vcenter"
				centered
                hidden={this.state.showAddWorkTime}
                visible={this.state.s}
			>
				<Modal.Header>
					<Modal.Title style={{ marginLeft: "37%" }} id="contained-modal-title-vcenter">
						{this.props.header}
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
			
					<UnsuccessAlert
						hidden={this.state.hiddenFailAlert}
						header={this.state.failHeader}
						message={this.state.failMessage}
						handleCloseAlert={this.handleCloseAlertFail}
					/>
					<div hidden={this.state.showAddWorkTime}>
						

						<table border="1" style={{ width: "100%" }}>
							<tr>
								<th>Pharmacy</th>
								<th>StartDate</th>
								<th>EndDate</th>
								<th>StartTime</th>
								<th>EndTime</th>
							</tr>
							{this.props.workTimesForStaff.map((workTime) => (
								<tr>
									<td>{workTime.EntityDTO.pharmacyName}</td>
									<td>{new Date(workTime.EntityDTO.startDate).toDateString()}</td>
									<td>{new Date(workTime.EntityDTO.endDate).toDateString()}</td>
									<td>{workTime.EntityDTO.startTime}</td>
									<td>{workTime.EntityDTO.endTime}</td>
								</tr>
							))}
						</table>

                       

                        
                        
					</div>

				{/*	<div hidden={!this.state.showAddWorkTime}>
						<form>
							<div className="control-group">
								<div className="form-row">
									<button onClick={() => this.handleBack()} className="btn btn-link btn-xl" type="button">
										<i className="icofont-rounded-left mr-1"></i>
										Back
									</button>
								</div>
								<table style={({ width: "100%" }, { marginLeft: "17%" })}>
									<tr>
										<td>
											<label>Date from:</label>
										</td>
										<td>
											<DatePicker
												className="form-control"
												style={{ width: "15em" }}
												minDate={new Date()}
												onChange={(date) => this.handleStartDateChange(date)}
												selected={this.state.selectedStartDate}
											/>
										</td>
									</tr>
									<tr>
										<td>
											<label>Date to:</label>
										</td>
										<td>
											<DatePicker
												className="form-control"
												style={{ width: "15em" }}
												minDate={this.state.selectedStartDate}
												onChange={(date) => this.handleEndDateChange(date)}
												selected={this.state.selectedEndDate}
											/>
										</td>
									</tr>
									<tr>
										<td>
											<label>Time from:</label>
										</td>
										<td>
											<input
												placeholder="Time from"
												className="form-control"
												style={{ width: "12.8em" }}
												type="number"
												min="1"
												max="23"
												onChange={this.handleTimeFromChange}
												value={this.state.timeFrom}
											/>
										</td>
									</tr>

									<tr>
										<td>
											<label>Time to:</label>
										</td>
										<td>
											<input
												placeholder="Time to"
												className="form-control"
												style={{ width: "12.8em" }}
												type="number"
												min="2"
												max="24"
												onChange={this.handleTimeToChange}
												value={this.state.timeTo}
											/>
										</td>
									</tr>
								</table>
								<div className="form-group text-center">
									<Button className="mt-3" onClick={() => this.handleAdd()}>
										Add worktime
									</Button>
								</div>
							</div>
						</form>
                            </div>*/}
                    <div class="modal-footer justify-content-between">
                    <ConfirmModal show={this.state.hiddenSuccessAlert}  visible={true} header="WorkTimes" />
          
      </div>
				</Modal.Body>
				<Modal.Footer>
                <button type="button" class="btn btn-secondary mr-auto" type="button"  onClick={() => this.handleOpenAlertSuccess()}>Remove Dermatologist</button>
                <Button onClick={() => this.handleAddWorkTimeModal()}>
							Add worktime
						</Button>

                        
                        <Button onClick={() => this.handleAddWorkTimeModal()}>
							Add Apointment
						</Button>
					<Button onClick={() => this.handleClickOnClose()}>Close</Button>
				</Modal.Footer>
			</Modal>



		);
	}
}

export default ScheduleModal;
