import React, { Component } from "react";
import Axios from "axios";
import Header from './Header';
import ReportImage from "../Images/report.png" ;
import GetAuthorisation from "../Funciton/GetAuthorisation";






class AdminStaffComplains extends Component {



  state = {
    complaints : [],
    date : new Date(),
    text : "",
    staffName : "",
    staffSurname : "",
    profession : "",
    reply : "",
    email : "",
  


};



  componentDidMount() {

    Axios.get("http://localhost:8080/complaint/getStaffComplaints", {
			validateStatus: () => true,
			headers: { Authorization: GetAuthorisation() },
		})
			.then((res) => {
				if (res.status === 401) {
          this.props.history.push('/login');
				} else {
					this.setState({ complaints: res.data });
					console.log(res.data);
				}
			})
			.catch((err) => {
				console.log(err);
			});



  }


  handleDrugClick = (drug) => {
		console.log(drug);
		this.setState({
			drugAmount: drug.EntityDTO.recommendedAmount,
			drugQuantity: drug.EntityDTO.quantity,
			drugManufacturer: drug.EntityDTO.manufacturer.EntityDTO.name,
			drugName: drug.EntityDTO.name,
			drugKind: drug.EntityDTO.drugKind,
			drugFormat: drug.EntityDTO.drugFormat,
			sideEffects: drug.EntityDTO.sideEffects,
			onReciept: drug.EntityDTO.onReciept,
			points: drug.EntityDTO.loyalityPoints,
			ingredients: drug.EntityDTO.ingredients,
			replacingDrugs: drug.EntityDTO.replacingDrugs,
			specificationModalShow: true,
		});
	};




    render() {
	

		return (
      
      <React.Fragment>

      <Header/>

      <h5 className=" text-center  mb-0 text-uppercase" style={{ marginTop: "2rem" }}>
						Staff complaints
				</h5>


        <table className="table" style={{ width: "50%", marginTop: "5em", marginLeft: "auto",marginRight: "auto" }}>
						<tbody>
							{this.state.complaints.map((complaint) => (
								<tr id={complaint.Id} key={complaint.Id} >
									<td width="130em">
										<img className="img-fluid" src={ReportImage} width="70em" />
									</td>

                  <td>
                                        
                                        <div>  
                                        <b>Patient's email: </b>{complaint.EntityDTO.email}
                        
                                        </div>
                                        <div>  
                                        <b>Employee's name: </b>{complaint.EntityDTO.staffName + "  " + complaint.EntityDTO.staffSurname}
                        
                                        </div> 
                                        <div>  
                                        <b>Complaint: </b>{complaint.EntityDTO.date}
                        
                                        </div>  

                                        

                                      </td>

                                      <td className="align-middle">
                                      <div style={{ marginLeft: "55%" }}>
                                        <button
                                          type="button"
                                          onClick={() => this.handleDrugClick(complaint)}
                                          className="btn btn-outline-secondary btn-block"
                                        >
                                          Reply
                                        </button>
                                      </div>
                                      
                                    </td>
									
									
								</tr>
							))}
						</tbody>
					</table>


      </React.Fragment>
        
		);
        }

}
export default AdminStaffComplains;
