import React, { Component } from "react";
import { Button, Modal } from "react-bootstrap";
import UnsuccessfulAlert from "../../Components/Alerts/UnsuccessfulAlert";
import DatePicker from "react-datepicker";

class ReservationDrugModal extends Component {
	
    state = {
		selectedDate: new Date(new Date().getTime() + 2 * 24 * 60 * 60 * 1000),
		drugAmount: 1,
	};

	handleDateChange = (date) => {
		this.setState({ selectedDate: date });
	};

	handleDrugAmountChange = (event) => {
		if (event.target.value < 1) this.setState({ drugAmount: 1 });
		else if (event.target.value > this.props.maxCount) this.setState({ drugAmount: this.props.maxCount });
		else this.setState({ drugAmount: event.target.value });
	};


	render() {
		return (
			<Modal
				show={this.props.show}
				size="lg"
				dialogClassName="modal-80w-80h"
				aria-labelledby="contained-modal-title-vcenter"
				centered
				onHide={this.props.onCloseModal}
			>
				<Modal.Header closeButton>
					<Modal.Title id="contained-modal-title-vcenter" >Drug reservation</Modal.Title>
				
				</Modal.Header>
				<Modal.Body>
					
                <div className="form-row mt-3">
								<div className="form-col" style={{ color: "#6c757d", opacity: 1 }}>
									<label>Drug reservation lasts until date:</label>
								</div>
							</div>
							<div className="form-row">
								<div className="form-col" style={{ color: "#6c757d", opacity: 1 }}>
									<DatePicker
										className="form-control mr-3"
										minDate={new Date(new Date().getTime() + 2 * 24 * 60 * 60 * 1000)}
										onChange={(date) => this.handleDateChange(date)}
										selected={this.state.selectedDate}
									/>
								</div>
							</div>
							<div className="form-row">
								<div className="form-col" style={{ color: "#6c757d", opacity: 1, fontSize: "0.7em" }}>
									Minimum period for reservation is 48h before, because you cannot pickup drug 24h before or less.
								</div>
							</div>
							<div className="form-row">
								<div className="form-col">
									<label>Drug amount:</label>
									<input
										placeholder="Drug amount"
										className="form-control mr-3"
										style={{ width: "9em" }}
										type="number"
										min="1"
										max={this.props.maxCount}
										onChange={this.handleDrugAmountChange}
										value={this.state.drugAmount}
									/>
								</div>
							</div>
							<div className="form-row mt-3">
								<div className="form-col">
									<h5>
										Total price:{" "}
										<b color="red">{(Math.round(this.state.drugAmount * this.props.drugPrice * 100) / 100).toFixed(2)} din</b>
									</h5>
								</div>
							</div>
							<div className="form-group text-center">
								<Button className="mt-3" onClick={() => this.props.reserveDrugs(this.state.drugAmount, this.state.selectedDate)}>
									Reserve drugs
								</Button>
							</div>


				</Modal.Body>
				<Modal.Footer>
                <div className="form-group text-center">
						<button
							hidden={this.props.hideSuccessfulModalButton}
							style={{ background: "#1977cc", marginTop: "15px" }}
							onClick={() => this.props.closeModal()}
							className="btn btn-primary btn-xl"
							id="sendMessageButton"
							type="button"
						>
							Ok
						</button>
					</div>
				</Modal.Footer>
			</Modal>

            
		);
	}
}

export default ReservationDrugModal;
