import React from 'react';
import './card.styles.css'
import AllergensModal from "../Components/Modal/AllergensModal";
state={
    openAllergenModal: false
}

handleAllergenModal = () => {
    this.setState({ openAllergenModal: true });
};
export const Card = (props) =>(

    <div className="card-style" onClick={this.handleClick}>
        <h1>{props.dermatologist.EntityDTO.name} {props.dermatologist.EntityDTO.surname}</h1>
        <AllergensModal show={this.state.openAllergenModal}/>
    </div>

   
  
)
