import React, { Component } from "react";
import Axios from "axios";
import Header from '../Components/Header';
import GetAuthorisation from "../Funciton/GetAuthorisation";
import DermatologistAppointmentPicture from "../Images/appointment.png" ;
import UnsuccessfulAlert from "../Components/Alerts/UnsuccessfulAlert";
import SuccessfulAlert from "../Components/Alerts/SuccessfulAlert";
import {NavLink, Redirect } from "react-router-dom";
import DatePicker from "react-datepicker";

const API_URL="http://localhost:8080";

class PharmaciesAppointmentStartPage extends Component {
	
  
    
    state = {
        
        pharmacyId: "",
        appointments : [],
        hiddenSuccessfulAlert: true,
        successfulHeader: "",
        successfulMessage: "",
        hiddenUnsuccessfulAlert: true,
        UnsuccessfulHeader: "",
        UnsuccessfulMessage: "",



    };

    constructor(props) {
        super(props);
    }
  


  componentDidMount() {

    if (!this.hasRole("ROLE_PATIENT")) {
			this.props.history.push('/login');
    }

  
	}
      

  hasRole = (requestRole) => {
    let currentRoles = JSON.parse(localStorage.getItem("keyRole"));

    if (currentRoles === null) return false;


    for (let currentRole of currentRoles) {
      if (currentRole === requestRole) return true;
    }
    return false;
  };


  handleCloseSuccessfulAlert = () => {
		this.setState({ hiddenSuccessfulAlert: true });
	};

	handleCloseUnsuccessfulAlert= () => {
		this.setState({ hiddenUnsuccessfulAlert: true });
	};



    
 
   

	render() {
	

		return (
      
      <React.Fragment>

         <Header/>
      
         <div className="container">



       


         <h1  className="text-center  mt-3  " >Please select the desired date for consultations with a pharmacist!</h1>
         
         
						
							<div className="form-col  mr-3">
								<div className="mb-2" style={{ fontSize: "1.5em" }}>
									Select date:
								</div>

								<DatePicker
									className="form-control"
									minDate={new Date()}
									onChange={(date) => this.handleDateChange(date)}
									selected={this.state.selectedDate}
								/>
							</div>
							<div className="form-col ml-2">
								<div className="mb-2" style={{ fontSize: "1.5em" }}>
									Hours:
								</div>
								<input
									onChange={this.handleHoursChange}
									className="form-control mr-3"
									value={this.state.hours}
									style={{ width: "9em" }}
									type="number"
									min="00"
									max="23"
								/>
							</div>
							<div className="form-col ml-2">
								<div className="mb-2" style={{ fontSize: "1.5em" }}>
									Minutes:
								</div>
								<input
									min="00"
									className="form-control"
									onChange={this.handleMinutesChange}
									value={this.state.minutes}
									style={{ width: "9em" }}
									type="number"
									max="59"
								/>
							</div>
						
						<div className="form-row justify-content-center mt-4">
							<div className="mt-1 text-danger" style={{ fontSize: "1.5em", display: this.state.showDateError }}>
								Invalid date or time! Desired time needs to be at least 1 hour from current time.
							</div>
						</div>
						<div className="form-row justify-content-center mt-4">
							<button className="btn btn-primary btn-lg" type="button" onClick={this.handleCheckAvailability}>
								<i className="icofont-search mr-1"></i>
								Check availability
							</button>
						</div>
					

          
        </div>
        </React.Fragment>
        
		);
	}
}

export default PharmaciesAppointmentStartPage;
