import QrReader from 'react-qr-reader'
import { withRouter } from "react-router";




class QrCode extends Component {


    state = {
		pharmacies: [],
		formShowed: false,
		name: "",
		city: "",
		openModal: false,
		openModalRefused: false,
		openModalCantUse: false,
		gradeFrom: "",
		gradeTo: "",
		distanceFrom: "",
		distanceTo: "",
		showingSearched: false,
		showingSorted: false,
		currentLatitude: null,
		currentLongitude: null,
		sortIndicator: 0,
		redirect:false,
		redirectUrl:''
	};

    componentDidMount() {

    }


    constructor(props){
        super(props)
        this.state = {
            delay: 100,
            result: 'No result',
        }
        this.handleScan = this.handleScan.bind(this)
    }

    openImageDialog() {
        this.refs.qrReader1.openImageDialog()
    }

    handleScan(data){
	    let RefuseReceiptDTO = {
	    	id: data
	    }
	    
        console.log(RefuseReceiptDTO);
		
    
	
    }



    render() {

        const previewStyle = {
            height: 240,
            width: 320,
        }
        return (
            <React.Fragment>
                <Header />

                <div className="container" style={{ marginTop: "10%" }}>
					<h5 className=" text-center mb-0 mt-2 text-uppercase">Enter QR code</h5>
       		<div className="container" style={{ marginTop: "0%" }}>
               
                
                
            </div>

            
            </div>

            <button
					type="button"
					onClick={this.openImageDialog.bind(this)}
					className="btn btn-outline-secondary mt-3"
				>
					Submit QR Code
				</button>


        </React.Fragment>
    );

    }

}
export default withRouter(QrCode);
