import React, { Component } from "react";
import Header from "../../Components/Header";
import Axios from "axios";
import GetAuthorisation from "../../Funciton/GetAuthorisation";
import orderImage from "../../Images/orderImage.png";
import 'react-confirm-alert/src/react-confirm-alert.css';
import { confirmAlert } from 'react-confirm-alert'; 
const API_URL = "http://localhost:8080";

class OrdersPageForPhAdmin extends Component {
	state = {
        orders: [],
        pharmacyId:'',
        hiddenSuccessAlert: true,
		successHeader: "",
		successMessage: "",
		hiddenFailAlert: true,
		failHeader: "",
		failMessage: "",
        offers:[],        
        showOffersModal: false,
        selectedOrder:'',
        drugsToAdd:[],
        addedDrugs:[],
        showEditOrder:false,
        drugs:[],
        showingSorted:false,
        drugsFromOrder:[],
        drugsForAdd:[],
        orderToEdit:'',
    };

    componentDidMount() {
		let pharmacyId=localStorage.getItem("keyPharmacyId")
		this.setState({
			pharmacyId: pharmacyId
		})
        Axios.get(API_URL + "/order/getAllOrdersForPharmacy/" + localStorage.getItem("keyPharmacyId"), {
			headers: { Authorization: GetAuthorisation() },
		})

			.then((res) => {
				console.log(res.data);
				this.setState({ orders: res.data });
			})
			.catch((err) => {
				console.log(err);
			});

    }

    handleRemoveOrder = (orderId) => {
        console.log(orderId)
        confirmAlert({
            message: 'Are you sure to remove order?',
            buttons: [
              {
                label: 'Yes',
                onClick: () => {
                   let IdDTO={
                       id:orderId
                   }
                    Axios
                    .put(API_URL + "/order/removeOrder",IdDTO, {
                        
                        validateStatus: () => true,
                        headers: { Authorization: GetAuthorisation() },
                    }).then((res) =>{
                        if (res.status === 200) {
                            this.setState({
                                hiddenSuccessAlert: false,
                                hiddenFailAlert:true,
                                successHeader: "Success",
                                successMessage: "You successfully delete order.",
                            })
                            this.updateOrders();
                        }else if(res.status === 400)
                        {
                            this.setState({
                                hiddenSuccessAlert: true,
                                hiddenFailAlert:false,
                                failHeader: "Unsuccess", 
                                failMessage: "Not possible to remove order because have offers"
                            })
                        }else if(res.status === 500)
                        {
                            this.setState({
                                hiddenSuccessAlert: true,
                                hiddenFailAlert:false,
                                failHeader: "Unsuccess", 
                                failMessage: "We have some problem. Try later."
                            })
                        }
                        
                    }).catch(() => {

                    });
                }
              },
              {
                label: 'No',
                onClick: () => {
                    
                }
              }
            ]
        });
    }

    updateOrders = () =>{
      /*  Axios
        .get(BASE_URL + "/api/order/find-orders-for-pharmacy?pharmacyId="+ this.state.pharmacyId, {
			headers: { Authorization: getAuthHeader() },
		}).then((res) =>{
            this.setState({orders : res.data});
            console.log(res.data);
        }).catch((err) => {console.log(err);});*/
    }

    
    handleCloseAlertSuccess = () => {
		this.setState({ hiddenSuccessAlert: true });
    };
    
    handleCloseAlertFail = () => {
		this.setState({ hiddenFailAlert: true });
	};

    handleCloseOffersModal =()=>{
        this.setState({ showOffersModal: false });

    }

    onViewOrdersClick = (id,num) => {
      /*  if(num===0){
            this.setState({
                hiddenSuccessAlert: true,
                hiddenFailAlert:false,
                failHeader: "Unsuccess", 
                failMessage: "There are currently no offers"
            })
        }else{
            Axios
            .get(BASE_URL + "/api/offer/offers-for-order?orderId="+ id, {
                validateStatus: () => true,
                headers: { Authorization: getAuthHeader() },
            }).then((res) =>{
                if(res.status ===200){
                    this.setState({
                        offers : res.data,
                        showOffersModal:true,
                        selectedOrder:id
                    });
                }
                if(res.status === 400)
                {
                    this.setState({
                        hiddenSuccessAlert: true,
                        hiddenFailAlert:false,
                        failHeader: "Unsuccess", 
                        failMessage: "The order has been processed"
                    })
                }else if(res.status === 500)
                {
                    this.setState({
                        hiddenSuccessAlert: true,
                        hiddenFailAlert:false,
                        failHeader: "Unsuccess", 
                        failMessage: "We have some problem. Try later."
                    })
                }
                console.log(res.data);
            }).catch((err) => {console.log(err);});    
        }*/
    }

    addItem = item => {

        this.setState({
            drugsFromOrder: [
            ...this.state.drugsFromOrder,
            item 
          ]
        })
    }

    removePeople(e) {
        var array = [...this.state.drugsForAdd]; // make a separate copy of the array
        var index = array.indexOf(e)
        if (index !== -1) {
          array.splice(index, 1);
          this.setState({drugsForAdd: array});
        }
      }

    handleRemoveFromDrugToAdd = (drug) =>{
        this.removePeople(drug)
     }

     handleAddToAddedDrugs = (drug) =>{
        this.addItem(drug);
     }

    onEditOrderClick = (order,num) =>{

    /*    if(num!==0){
            this.setState({
                hiddenSuccessAlert: true,
                hiddenFailAlert:false,
                failHeader: "Unsuccess", 
                failMessage: "Not possible to remove order because have offers"
            })
        }else{
            Axios
            .get(BASE_URL + "/api/order/find-drugs-by-order?orderId="+ order.Id, {
                headers: { Authorization: getAuthHeader() },
            }).then((res) =>{
                this.setState({drugsFromOrder : res.data});
                console.log(res.data);
            }).catch((err) => {console.log(err);});
    
            Axios
            .get(BASE_URL + "/api/order/find-drugs-by-order-to-add?orderId="+ order.Id, {
                headers: { Authorization: getAuthHeader() },
            }).then((res) =>{
                this.setState({drugsForAdd : res.data});
                console.log(res.data);
            }).catch((err) => {console.log(err);});
    
    
            this.setState({
                orderToEdit: order.Id,
                showEditOrder:true,
            })
        }
    }

    handleAcceptOffer = () =>{
        this.setState({
            hiddenSuccessAlert: false,
            hiddenFailAlert:true,
            successHeader: "Success",
            successMessage: "You successfully accept offer.",
            showOffersModal:false
        })
        this.updateOrders();
    }

    handleResetFilter = () =>{
        Axios
        .get(BASE_URL + "/api/order/find-orders-for-pharmacy?pharmacyId="+ this.state.pharmacyId, {
			headers: { Authorization: getAuthHeader() },
		}).then((res) =>{
            this.setState({orders : res.data,showingSorted:false});
            console.log(res.data);
        }).catch((err) => {console.log(err);});
    }

    handleFilterOrderPerCreated = () =>{
        Axios
        .get(BASE_URL + "/api/order/find-created-orders-for-pharmacy?pharmacyId="+ this.state.pharmacyId, {
			headers: { Authorization: getAuthHeader() },
		}).then((res) =>{
            this.setState({orders : res.data,showingSorted:true});
            console.log(res.data);
        }).catch((err) => {console.log(err);});
    }

    handleFilterOrderPerProccessed = () =>{
        Axios
        .get(BASE_URL + "/api/order/find-processed-orders-for-pharmacy?pharmacyId="+ this.state.pharmacyId, {
			headers: { Authorization: getAuthHeader() },
		}).then((res) =>{
            this.setState({orders : res.data,showingSorted:true});
            console.log(res.data);
        }).catch((err) => {console.log(err);});*/
    }

    handleEditOrderModalClose = () =>{
        this.setState({
            showEditOrder:false
        })
    }

    render() {
		return (
        <React.Fragment>
            <div>

            </div>
                    <Header />
                
					<div className="container" style={{ marginTop: "2%" }}>
					<h5 className=" text-center mb-0 mt-2 text-uppercase">Orders </h5>

					<table className="table table-hover" style={{ width: "100%", marginTop: "3rem" }}>
						<tbody>
							{this.state.orders.map((order) => (
								<tr className="rounded">
									<td width="190em">
										<img className="img-fluid" src={orderImage}  width="150em" />
									</td>
									<td>
										<div>
											<b>Date : </b>{" "}
											{new Date(order.EntityDTO.dateTo).toLocaleTimeString("en-US", {
													day: "2-digit",
													month: "2-digit",
													year: "numeric",
													hour: "2-digit",
													minute: "2-digit",
												})}
										</div>
										
										<div>
											<b>Creator: </b> {order.EntityDTO.creator}
										</div>

                                        <div>
											<b>Offers: </b> {order.EntityDTO.offersCount}
										</div>

                                        <div>
											<b>Status: </b> {order.EntityDTO.orderStatus}
										</div>
									</td>
									<td className="align-middle">
										
										<div className="mt-2" >
											<button
												type="button"
												onClick={() => this.handleRemoveOrder(order.Id)}
												className="btn btn-outline-secondary"
											>
												Remove Order
											</button>
										</div>
									</td>
								</tr>
							))}
						</tbody>
					</table>

				</div>
                    
                </React.Fragment>
		);
	}
}

