import Header from "../../Components/Header";
import React, { Component } from "react";
import Axios from "axios";
import { Redirect } from "react-router-dom";
import GetAuthorisation from "../../Funciton/GetAuthorisation";
import { Button, Modal } from 'react-bootstrap';


class AddPharmacistModal extends Component {

    state = {
        errorHeader: "",
        errorMessage: "",
        hiddenErrorAlert: true,
        email: "",
        password: "",
        name: "",
        userRegister: "",
        surname: "",
        address: "",
        openModalData: false,
        phoneNumber: "",
        emailError: "none",
        passwordError: "none",
        nameError: "none",
        surnameError: "none",
        addressError: "none",
        phoneError: "none",
        employeeError: "none",
        emailNotValid: "none",
        openModal: false,
        pharmacies: [],
        selectedPharmacy: null,
        pharmacy: "",
        coords: [],
        selectValue: "",
        redirect: false,
        redirectUrl: '',
    };

    constructor(props) {
        super(props);
        this.addressInput = React.createRef();
    }


    handleModalDataClose = () => {
        this.setState({
            openModalData: false,
        });
    };

    handleModalEmailClose = () => {
        this.setState({
            openModalEmail: false,
        });
    };

    componentDidMount() {

  

    }


    handleEmailChange = (event) => {
        this.setState({ email: event.target.value });
    };
    handlePharmacyChange = (event) => {
        this.setState({ pharmacy: event.target.value });
    };

    handlePasswordChange = (event) => {
        this.setState({ password: event.target.value });
    };

    handleNameChange = (event) => {
        this.setState({ name: event.target.value });
    };

    handleSurnameChange = (event) => {
        this.setState({ surname: event.target.value });
    };

    handleAddressChange = (event) => {
        this.setState({ address: event.target.value });
    };

    handlePhoneNumberChange = (event) => {
        this.setState({ phoneNumber: event.target.value });
    };



    validateForm = (userDTO) => {

        this.setState({
            emailError: "none",
            emailNotValid: "none",
            nameError: "none",
            surnameError: "none",
            addressError: "none",
            phoneError: "none",

        });

        if (userDTO.email === "") {
            this.setState({ emailError: "initial" });
            return false;
        } else if (!userDTO.email.includes("@")) {
            this.setState({ emailNotValid: "initial" });
            return false;
        } else if (userDTO.name === "") {
            this.setState({ nameError: "initial" });
            return false;
        } else if (userDTO.surname === "") {
            this.setState({ surnameError: "initial" });
            return false;
        } else if (userDTO.address === "") {
            this.setState({ addressError: "initial" });
            return false;
        } else if (userDTO.phoneNumber === "") {
            this.setState({ phoneError: "initial" });
            return false;
        }

        return true;
    };

    handleModalClose = () => {
        this.setState({ openModal: false });
    };



    validateEmployee = (employee) => {

        this.setState({
            employeeError: "none"
        });

        if (employee === "") {
            this.setState({ employeeError: "initial" });
            return false;
        }
        return true;
    };

    handleSignUp = () => {


        let userDTO = {
            email: this.state.email,
            name: this.state.name,
            surname: this.state.surname,
            address: this.state.address,
            phoneNumber: this.state.phoneNumber,
            password: this.state.password,
        };
        if (this.validateForm(userDTO) && this.validateEmployee(this.state.selectValue)) {

            if (this.state.selectValue == "dermathologist") {

                Axios.post("http://localhost:8080/reg/signup-dermathologist", userDTO, { headers: { Authorization: GetAuthorisation() } })
                    .then((res) => {
                        if (res.status === 400) {
                            this.setState({
                                errorHeader: "Resource conflict!",
                                errorMessage: "Email already exist.",
                                hiddenErrorAlert: false,
                            });
                        } else if (res.status === 500) {
                            console.log("USO")
                            this.setState({ openModalData: true });
                        } else {
                            console.log("Success");
                            this.setState({
                                openModal: true,
                                userRegister: "You have successfully registered staff with password " + res.data,
                                redirect: true
                            });
                        }
                    })
                    .catch((err) => {
                        console.log(err);
                    });
            }

        };


    };
    handleCloseAlert = () => {
        this.setState({ hiddenErrorAlert: true });
    };
    handleSelectChange = (event) => {
        this.setState({ selectValue: event.target.value });
    };


    render() {

        return (

            <Modal show={this.props.show} centered size={"lg"}>
                
        
                <Modal.Header >
                    <Modal.Title style={{ marginLeft: '19%' }} >
                        {this.props.header}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className="container">
                        <div className="container" style={{ marginTop: "2%" }}>




                            <h5 className=" text-center  mb-0 text-uppercase" style={{ marginTop: "2rem" }}>
                                Register staff
                            </h5>



                            <div className="row section-design">
                                <div className="col-lg-8 mx-auto">
                                    <br />
                                    <form id="contactForm" name="sentMessage" novalidate="novalidate">
                                        <div className="control-group">
                                            <div className="form-group controls mb-0 pb-2" style={{ color: "#6c757d", opacity: 1 }}>
                                                <label>Name:</label>
                                                <input
                                                    placeholder="Name"
                                                    class="form-control"
                                                    type="text"
                                                    id="name"
                                                    onChange={this.handleNameChange}
                                                    value={this.state.name}
                                                />
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.nameError }}>
                                                Name must be entered.
                                            </div>
                                        </div>
                                        <div className="control-group">
                                            <div className="form-group controls mb-0 pb-2" style={{ color: "#6c757d", opacity: 1 }}>
                                                <label>Surname:</label>
                                                <input
                                                    placeholder="Surname"
                                                    class="form-control"
                                                    type="text"
                                                    id="surname"
                                                    onChange={this.handleSurnameChange}
                                                    value={this.state.surname}
                                                />
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.surnameError }}>
                                                Surname must be entered.
                                            </div>
                                        </div>

                                        <div className="control-group">
                                            <div className="form-group controls mb-0 pb-2" style={{ color: "#6c757d", opacity: 1 }}>
                                                <label>Address:</label>
                                                <input
                                                    placeholder="Address"
                                                    class="form-control"
                                                    type="text"
                                                    id="address"
                                                    onChange={this.handleAddressChange}
                                                    value={this.state.address}
                                                />
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.addressError }}>
                                                Address must be entered.
                                            </div>
                                        </div>


                                        <div className="control-group">
                                            <div className="form-group controls mb-0 pb-2" style={{ color: "#6c757d", opacity: 1 }}>
                                                <label>Email address:</label>
                                                <input
                                                    placeholder="Email address"
                                                    className="form-control"
                                                    id="email"
                                                    type="text"
                                                    onChange={this.handleEmailChange}
                                                    value={this.state.email}
                                                />
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.emailError }}>
                                                Email address must be entered.
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.emailNotValid }}>
                                                Email address is not valid.
                                            </div>
                                        </div>

                                        <div className="control-group">
                                            <div className="form-group controls mb-0 pb-2" style={{ color: "#6c757d", opacity: 1 }}>
                                                <label>Phone number:</label>
                                                <input
                                                    placeholder="Phone number"
                                                    class="form-control"
                                                    id="phone"
                                                    type="text"
                                                    onChange={this.handlePhoneNumberChange}
                                                    value={this.state.phoneNumber}
                                                />
                                            </div>
                                            <div className="text-danger" style={{ display: this.state.phoneError }}>
                                                Phone number must be entered.
                                            </div>
                                        </div>

                                        <div className="form-group">
                                            <button
                                                style={{
                                                    background: "#1977cc",
                                                    marginTop: "15px",
                                                    marginLeft: "40%",
                                                    width: "20%",
                                                }}
                                                onClick={this.handleSignUp}
                                                className="btn btn-primary btn-xl"
                                                id="sendMessageButton"
                                                type="button"
                                            >
                                                Sign Up
                                            </button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                       </div> 
                </Modal.Body>
                    <Modal.Footer>
                        <Button onClick={() => this.handleClickOnClose()}>Close</Button>
                    </Modal.Footer>
            </Modal>
         );
    }
}

export default AddPharmacistModal;
