import React, { Component } from "react";
import { Button, Modal } from "react-bootstrap";
import Header from '../Header';
import Axios from "axios";
import GetAuthorisation from "../../Funciton/GetAuthorisation";
import { CardList } from "../card-components/card-list.component";
const API_URL="http://localhost:8080";


class DermatologistsForPhAdmin extends Component {
	state = {
        dermatologists: [],
        staffIdForWorkTimes:'',
        showWorkTimesModal: false,
        showCreateAppointmentModal: false,
        showAddDermatologistModal: false,
        workTimes:[],
        forStaff:'',
        formShowed:false,
        searchName:'',
        searchSurname:'',
        searchGradeFrom:'',
        searchGradeTo:'',
        showingSearched: false,
        pharmaciesForDermatologist:[],
        showPharmaciesModal:false,
        dermatologistToEmploye:[],
        showingSorted:false,
        hiddenSuccessAlert: true,
		successHeader: "",
		successMessage: "",
		hiddenFailAlert: true,
		failHeader: "",
		failMessage: "",
    };

    handleCloseAlertSuccess = () => {
		this.setState({ hiddenSuccessAlert: true });
    };
    
    handleCloseAlertFail = () => {
		this.setState({ hiddenFailAlert: true });
	};

     
    componentDidMount() {

        let pharmacyId=localStorage.getItem("keyPharmacyId")
		this.setState({
			pharmacyId: pharmacyId
		})

		Axios.get(API_URL + "/users/dermatologistsInPharmacy/" +localStorage.getItem("keyPharmacyId"), {
			headers: { Authorization:  GetAuthorisation() },
		})
			.then((res) => {
				this.setState({ dermatologists: res.data });
                console.log(res.data);
            
			})
			.catch((err) => {
				console.log(err);
			});
    }




 
    handleModalClose = () => {
        this.setState({showWorkTimesModal: false});
    }

   

    hangleFormToogle = () => {
		this.setState({ formShowed: !this.state.formShowed });
    };
    
 
    

    
    render() {
        const myStyle = {
			color: "white",
			textAlign: "center",
		};
		return (
        <React.Fragment>
            

                   
                    <Header />

                

                    
                    <div className="container" style={{ marginTop: "5%" , marginBottom:"5%",marginLeft:"6%"}} >
                  
                       

                        <button
                            className="btn btn-outline-primary btn-xl"
                            type="button"
                            onClick={this.hangleFormToogle}
                        >
                            <i className="icofont-rounded-down mr-1"></i>
                            Search dermatologists
                        </button>
                        <form
                            className="form-inline mt-3 collapse"
                
                            width="100%"
                            id="formCollapse"
                            >
                            <div className="form-group mb-2" width="100%">
                                <input
                                    placeholder="Name"
                                    className="form-control mr-2"
                                    style={{ width: "9em" }}
                                    type="text"
                                    onChange={this.handleNameChange}
                                    value={this.state.searchName}
                                />

                                <input
                                    placeholder="LastName"
                                    className="form-control mr-2"
                                    style={{ width: "9em" }}
                                    type="text"
                                    onChange={this.handleSurnameChange}
                                    value={this.state.searchSurname}
                                />

                                <input
                                    placeholder="Grade from"
                                    className="form-control mr-2"
                                    style={{ width: "9em" }}
                                    type="number"
                                    min="1"
                                    max="5"
                                    onChange={this.handleGradeFromChange}
                                    value={this.state.searchGradeFrom}
                                />
                                <input
                                    placeholder="Grade to"
                                    className="form-control mr-2"
                                    style={{ width: "9em" }}
                                    type="number"
                                    min="1"
                                    max="5"
                                    onChange={this.handleGradeToChange}
                                    value={this.state.searchGradeTo}
                                />

                           
                            </div>
                            <div style={{marginLeft:'1%'}}>

                                <button
                                    style={{ background: "#1977cc" },{marginLeft:'15%'},{marginBottom:'8%'}}
                                    onClick={this.handleSearchClick}
                                    className="btn btn-primary btn-x2"
                                    type="button"
                                        >
                                    <i className="icofont-search mr-1"></i>
                                    Search
                                </button>
                            </div>

                        </form>
                        <Button style={{marginLeft:"1%"}} onClick={() => this.handleAddWorkTimeModal()}>
							Add dermatologist
						</Button>
                        <div
						className={
							this.state.showingSearched
								? "form-group mt-2"
								: "form-group mt-2 collapse"
						}
					>
						<button
							type="button"
							className="btn btn-outline-secondary"
							onClick={this.handleResetSearch}
						>
							<i className="icofont-close-line mr-1"></i>Reset criteria
						</button>
					</div>

                         
                    </div>
                    <div className="container">
                    <CardList dermatologists={this.state.dermatologists} pharmacyId={this.state.pharmacyId} /> 
           
                    </div>
                    
 
                </React.Fragment>
		);
	}
}

export default DermatologistsForPhAdmin
