import Header from "../../Components/Header";
import React, { Component } from "react";
import Axios from "axios";
import { Redirect } from "react-router-dom";
import GetAuthorisation from "../../Funciton/GetAuthorisation";



class registerStaff extends Component {

    state = {
		errorHeader: "",
		errorMessage: "",
		hiddenErrorAlert: true,
		email: "",
		password: "",
		name: "",
		userRegister: "",
		surname: "",
		address: "",
		openModalData: false,
		phoneNumber: "",
		emailError: "none",
		passwordError: "none",
		nameError: "none",
		surnameError: "none",
		addressError: "none",
		phoneError: "none",
		emailNotValid: "none",
		openModal: false,
		pharmacies:[],
		selectedPharmacy:null,
		pharmacy:"",
		coords: [],
		selectValue:"",
        redirect: false,
        redirectUrl: '',
	};

	constructor(props) {
		super(props);
		this.addressInput = React.createRef();
	}

	
	handleModalDataClose = () => {
		this.setState({ 
			openModalData: false,
		});
	};

	handleModalEmailClose = () => {
		this.setState({ 
			openModalEmail: false,
		});
	};
	
	componentDidMount() {

		Axios.get("http://localhost:8080/pharmacy/allPharmacies")
			.then((res) => {
				this.setState({ pharmacies: res.data });
				console.log(res.data);
			})
			.catch((err) => {
				console.log(err);
			});

		Axios.get("/api/users/sysadmin/auth", { validateStatus: () => true, headers: { Authorization: GetAuthorisation() } })
            .then((res) => {
				console.log(res.statusm, "TEST")
                if (res.status === 401) {
                    this.setState({
                        redirect: true,
                        redirectUrl: "/unauthorized"
                    });
                } else {
                    console.log(res.data);
                }
            })
            .catch((err) => {
                console.log(err);
