import React, { Component } from "react";
import Axios from "axios";
import Header from './Header';
import CapsuleLogo from "../Images/medicament.jpg" ;
const API_URL="http://localhost:8080";

class Drugs extends Component {
	
  
    
    state = {
		allDrugs: []
		


  }
  componentDidMount() {
		

		Axios.get(API_URL + "/drug/allDrugs")

			.then((res) => {
				this.setState({ allDrugs: res.data });
			})
			.catch((err) => {
				console.log(err);
			});

	}




	render() {
	

		return (
      
      <React.Fragment>

      <Header/>
      
      <div id="allDrugs">

        


        <div className="container">
                    <h1 >All drugs</h1>
                    <table className="table" style={{ width: "100%", marginTop: "3rem" }}>
                        
                        <tbody>
                            {
                                this.state.allDrugs.map((drug) => (
                                    <tr key={drug.Id} id={drug.Id} >
                                       

                                      <td  width="150em">
                                        
                                        <img src={CapsuleLogo}   width="70em"></img>
                                        

                                      </td>


                                      <td>
                                        
                                        <div>  
                                        <b>Name: </b>{drug.EntityDTO.name}
                                        </div>  

                                        <div>  
                                        <b>Producer name: </b> {drug.EntityDTO.producerName}
                                        </div> 

                                        <div>  
                                        <b>Fabric code: </b>{drug.EntityDTO.fabricCode}
                                        </div>

                                      </td>


                                    </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
        </div>
        </React.Fragment>
        
		);
	}
}

export default Drugs;
