import React, { Component } from "react";
import Modal from 'react-bootstrap/Modal'
import './card.styles.css'
import Picture from "../../Images/dermatologist.png" ;
import ScheduleModal from "../Modal/ScheduleModal";

const API_URL="http://localhost:8080";
class Card extends React.Component {
	
    constructor(props){
        super(props);
    }
    
    
    state = {

        openAllergenModal: false,
        workTimes:[],
        employee: ''
  };


  


    handleAllergenModal = (id) => {
        this.setState({openAllergenModal: true });
        console.log("aaaaaaaaaaaaaa");
        console.log(id);
        Axios.get(API_URL + "/users/scheduleForEmployee/" + id, {
			headers: { Authorization: getAuthHeader() },
		})
        .then((res) => {
            this.setState({ workTimes: res.data , employee:id});
            console.log(res.data);
            console.log(id);
        })
        .catch((err) => {
            console.log(err);
        });
        this.setState({
            showWorkTimesModal: true
        });
        };



	render() {
	

		return (
      
      <React.Fragment>
        <div className="card-style" onClick={() => this.handleAllergenModal(this.props.dermatologist.Id)}>
        <img className="img-fluid" src={Picture}  />
        <h2>{this.props.dermatologist.EntityDTO.name} {this.props.dermatologist.EntityDTO.surname}</h2>
        <p>{this.props.dermatologist.EntityDTO.email}</p>
        <p>{this.props.dermatologist.EntityDTO.phoneNumber}</p>
        <p>{this.props.dermatologist.EntityDTO.grade} </p>
       
        
        <Modal
        show={this.state.openAllergenModal}
        
        dialogClassName="modal-90w"
        aria-labelledby="example-custom-modal-styling-title"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-custom-modal-styling-title">
            Custom Modal Styling
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
            commodi aspernatur enim, consectetur. Cumque deleniti temporibus
            ipsam atque a dolores quisquam quisquam adipisci possimus
            laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
            accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
            reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
            deleniti rem!
          </p>
        </Modal.Body>
      </Modal>
        </div>
     
	    </React.Fragment>
        

		);
	}
}

export default Card;
