import React, { Component } from "react";
import Modal from 'react-bootstrap/Modal'
import './card.styles.css'
import Picture from "../../Images/dermatologist.png" ;
import ScheduleModal from "../Modal/ScheduleModal";
import Header from '../Header';
import Axios from "axios";
import GetAuthorisation from "../../Funciton/GetAuthorisation";

const API_URL="http://localhost:8080";
class Card extends React.Component {
	
    constructor(props){
        super(props);
    }
    
    
    state = {

        openAllergenModal: false,
        workTimes:[],
        employee: ''
  };


  


    handleAllergenModal = (id) => {
        this.setState({openAllergenModal: true });
        console.log("aaaaaaaaaaaaaa");
        console.log(id);
        Axios.get(API_URL + "/users/scheduleForEmployee/" + id, {
			headers: { Authorization: GetAuthorisation() },
		})
        .then((res) => {
            this.setState({ workTimes: res.data , employee:id});
            console.log(res.data);
            console.log(id);
        })
        .catch((err) => {
            console.log(err);
        });
        this.setState({
            showWorkTimesModal: true
        });
        };



	render() {
	

		return (
      
      <React.Fragment>
        <button className="card-style" onClick={() => this.handleAllergenModal(this.props.dermatologist.Id)}>
        <img className="img-fluid" src={Picture}  />
        <h2>{this.props.dermatologist.EntityDTO.name} {this.props.dermatologist.EntityDTO.surname}</h2>
        <p>Email: {this.props.dermatologist.EntityDTO.email}</p>
        <p>Phone: {this.props.dermatologist.EntityDTO.phoneNumber}</p>
        <p>Grade: {this.props.dermatologist.EntityDTO.grade} </p>
        
        
        </button>
        <ScheduleModal show={this.state.openAllergenModal} updateWorkTimes={this.handleUpdateWorkTime} onCloseModal={this.handleModalClose} workTimesForStaff={this.state.workTimes}  forStaff={this.state.employee} header="WorkTimes" />
	    </React.Fragment>
        

		);
	}
}

export default Card;
